import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import emailjs from '@emailjs/browser';
import CoffeeParticles from './CoffeeParticles';
import './Contact.css';

const Contact = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    message: ''
  });

  const [isSubmitting, setIsSubmitting] = useState(false);
  const [submitStatus, setSubmitStatus] = useState(null);

  // EmailJS configuration - Replace these with your actual values
  const EMAILJS_CONFIG = {
    serviceId: 'YOUR_SERVICE_ID', // Get from EmailJS dashboard
    templateId: 'YOUR_TEMPLATE_ID', // Get from EmailJS dashboard
    publicKey: 'YOUR_PUBLIC_KEY' // Get from EmailJS dashboard
  };

  useEffect(() => {
    // Initialize EmailJS if keys are configured
    if (EMAILJS_CONFIG.publicKey !== 'YOUR_PUBLIC_KEY') {
      emailjs.init(EMAILJS_CONFIG.publicKey);
    }
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    setSubmitStatus(null);

    try {
      // Check if EmailJS is configured
      if (EMAILJS_CONFIG.publicKey === 'YOUR_PUBLIC_KEY') {
        // Fallback: Simulate email sending and show success
        await new Promise(resolve => setTimeout(resolve, 2000));

        setSubmitStatus({
          type: 'success',
          message: 'Thank you! Your message has been sent successfully. (Demo mode - configure EmailJS for real emails)'
        });

        // Clear form
        setFormData({ name: '', email: '', message: '' });
      } else {
        // Real EmailJS integration
        const templateParams = {
          from_name: formData.name,
          from_email: formData.email,
          message: formData.message,
          to_name: "Tan Ton"
        };

        const result = await emailjs.send(
          EMAILJS_CONFIG.serviceId,
          EMAILJS_CONFIG.templateId,
          templateParams,
          EMAILJS_CONFIG.publicKey
        );

        if (result.status === 200) {
          setSubmitStatus({
            type: 'success',
            message: 'Thank you! Your message has been sent successfully.'
          });

          // Clear form
          setFormData({ name: '', email: '', message: '' });
        } else {
          throw new Error('Failed to send email');
        }
      }
    } catch (error) {
      console.error('Email error:', error);
      setSubmitStatus({
        type: 'error',
        message: 'Sorry, there was an error sending your message. Please try again.'
      });
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <section id="contact" className="contact">
      <CoffeeParticles count={1} />
      <div className="container">
        <motion.h2 
          className="section-title"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
          viewport={{ once: true }}
        >
          Get In Touch
        </motion.h2>
        <motion.p 
          className="section-subtitle"
          initial={{ opacity: 0 }}
          whileInView={{ opacity: 1 }}
          transition={{ delay: 0.2 }}
          viewport={{ once: true }}
        >
          Let's connect and discuss opportunities
        </motion.p>

        <div className="contact-content">
          <motion.div 
            className="contact-info"
            initial={{ opacity: 0, x: -50 }}
            whileInView={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.6 }}
            viewport={{ once: true }}
          >
            <div className="contact-item">
              <i className="fas fa-envelope"></i>
              <div>
                <h4>Email</h4>
                <p>ttanton2001@gmail.com</p>
              </div>
            </div>

            <div className="contact-item">
              <i className="fas fa-phone"></i>
              <div>
                <h4>Phone</h4>
                <p>+1 (669) 203-7383</p>
              </div>
            </div>

            <div className="contact-item">
              <i className="fas fa-map-marker-alt"></i>
              <div>
                <h4>Location</h4>
                <p>Elk Grove, CA</p>
              </div>
            </div>

            <div className="contact-item">
              <i className="fab fa-linkedin"></i>
              <div>
                <h4>LinkedIn</h4>
                <a href="https://www.linkedin.com/in/tanthatthienton/" target="_blank" rel="noopener noreferrer">
                  linkedin.com/in/tanthatthienton
                </a>
              </div>
            </div>

            <div className="contact-item">
              <i className="fab fa-github"></i>
              <div>
                <h4>GitHub</h4>
                <a href="https://github.com/TinTinTheGuy" target="_blank" rel="noopener noreferrer">
                  github.com/TinTinTheGuy
                </a>
              </div>
            </div>
          </motion.div>

          <motion.form 
            className="contact-form"
            initial={{ opacity: 0, x: 50 }}
            whileInView={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.6 }}
            viewport={{ once: true }}
            onSubmit={handleSubmit}
          >
            <div className="form-group">
              <input 
                type="text" 
                placeholder="Your Name" 
                value={formData.name}
                onChange={(e) => setFormData({...formData, name: e.target.value})}
                required 
              />
            </div>
            <div className="form-group">
              <input 
                type="email" 
                placeholder="Your Email" 
                value={formData.email}
                onChange={(e) => setFormData({...formData, email: e.target.value})}
                required 
              />
            </div>
            <div className="form-group">
              <textarea 
                placeholder="Your Message" 
                rows="5" 
                value={formData.message}
                onChange={(e) => setFormData({...formData, message: e.target.value})}
                required
              ></textarea>
            </div>
            <motion.button 
              type="submit" 
              className="btn btn-primary"
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              disabled={isSubmitting}
            >
              <i className={isSubmitting ? "fas fa-spinner fa-spin" : "fas fa-paper-plane"}></i>
              <span>{isSubmitting ? "Sending..." : "Send Message"}</span>
            </motion.button>
            {submitStatus && (
              <div className={`submit-status ${submitStatus.type}`}>
                <i className={`fas ${submitStatus.type === 'success' ? 'fa-check-circle' : 'fa-exclamation-circle'}`}></i>
                <span>{submitStatus.message}</span>
              </div>
            )}
            {EMAILJS_CONFIG.publicKey === 'YOUR_PUBLIC_KEY' && (
              <div className="emailjs-notice">
                <p><small>💡 To enable real email sending, configure EmailJS in the Contact.js file</small></p>
              </div>
            )}
          </motion.form>
        </div>
      </div>
    </section>
  );
};

export default Contact;
